mapper.update:
update absense
set date = #{date,jdbcType=TIMESTAMP},
  employee_id = #{employeeId,jdbcType=INTEGER},
  type = #{type,jdbcType=CHAR},
  remarks = #{remarks,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, date, employee_id, type, remarks
from absense
where id = #{id,jdbcType=INTEGER}
