mapper.update:
update check_off
set tax = #{tax,jdbcType=DOUBLE},
  insurance = #{insurance,jdbcType=DOUBLE},
  housing_fund = #{housingFund,jdbcType=DOUBLE},
  basic_salary_id = #{basicSalaryId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, tax, insurance, housing_fund, basic_salary_id
from check_off
where id = #{id,jdbcType=INTEGER}
